#include<bits/stdc++.h>



using namespace std;



int main(){


	int a,b,c;


	cin>>a>>b>>c;


	if(a+b+c!=180)


		cout<<'Error';


	else if(a==90&&b==45 || b==90&&a==45 || c==90&&a==45)


		cout<<'Isosceles right';


	else if(a==60&&b==60)


		cout<<'Equilateral';


	else if(a==90||b==90||c==90)


		cout<<'Right';


	else if(a==b||a==c||b==c)


		cout<<'Isosceles'; 


	else 	cout<<'Scalene';		


	return 0;
}。

#include <bits/stdc++.h>
using namespace std;
int  fb(int n){
    int a[1000001];
    a[1]=1;
    a[2]=1;
    for(int i=3;i<=n;i++){
        a[i]=a[i-1]+a[i-2];
        if(a[i]>=10007){
            a[i]-=10007;
        }
    }
    cout<<a[n];
}
int main() {
    int n;
    cin>>n;
    fb(n);
 return 0;
}。
